INSERT INTO `t_s_function` (`ID`, `functioniframe`, `functionlevel`, `functionname`, `functionorder`, `functionurl`, `parentfunctionid`, `iconid`, `desk_iconid`, `functiontype`) VALUES ('402848814d22d92f014d22dae8220001', NULL, 1, '收发引擎监控', '1', 'monitorController.do?monitor', '8a8ab0b246dc81120146dc8180d9001d', '8a8ab0b246dc81120146dc8180460000', '8a8ab0b246dc81120146dc8180dd001e', 0);
create view ddo_channel_complaint_view as select `t1`.`id` AS `id`,`t1`.`channel_id` AS `channel_id`,`t1`.`sum_amount` AS `sum_amount`,`t1`.`sum_date` AS `sum_date`,`t1`.`msisdn_num` AS `msisdn_num`,(case when (`t2`.`num` is not null) then `t2`.`num` else 0 end) AS `num`,`t2`.`id` AS `channel_complaint_id`,(case when (`t2`.`num` is not null) then round(((`t2`.`num` / `t1`.`msisdn_num`) * 10000),2) else 0 end) AS `scale` from (`ddo_channel_statistics_day` `t1` left join `ddo_channel_complaint_day` `t2` on(((`t1`.`channel_id` = `t2`.`channel_id`) and (`t1`.`sum_date` = `t2`.`sum_date`))));

create view ddo_full_complaint_view as select `t1`.`id` AS `id`,`t1`.`sum_amount` AS `sum_amount`,`t1`.`sum_date` AS `sum_date`,`t1`.`msisdn_num` AS `msisdn_num`,(case when (`t2`.`num` is not null) then `t2`.`num` else 0 end) AS `num`,`t2`.`id` AS `full_complaint_id`,(case when (`t2`.`num` is not null) then round(((`t2`.`num` / `t1`.`msisdn_num`) * 10000),2) else 0 end) AS `scale` from (`ddo_full_statistics_day` `t1` left join `ddo_full_complaint_day` `t2` on((`t1`.`sum_date` = `t2`.`sum_date`)));