create view ddo_msg_view as select `t1`.`id` AS `id`,`t1`.`msisdn` AS `msisdn`,`t1`.`billing_business_id` AS `billing_business_id`,`t1`.`return_msg_code` AS `return_msg_code`,`t1`.`transation_id` AS `transation_id`,`t1`.`send_time` AS `send_time`,`t1`.`bill_state_code` AS `bill_state_code`,`t1`.`bill_state_time` AS `bill_state_time`,`t1`.`send_result` AS `send_result`,`t1`.`msisdn_province_code` AS `msisdn_province_code`,`t1`.`msisdn_city_code` AS `msisdn_city_code`,`t1`.`channel_id` AS `channel_id`,`t1`.`request_id` AS `request_id`,`t1`.`repeat_flag` AS `repeat_flag`,`t1`.`create_date` AS `create_date`,`t1`.`response_time` AS `response_time`,`t2`.`name` AS `channel_name` from (`ddo_msg` `t1` join `ddo_channel` `t2`) where (`t1`.`channel_id` = `t2`.`id`);
create view ddo_channel_statistics_month_view as select `t1`.`id` AS `id`,`t1`.`sum_month` AS `sum_month`,`t1`.`channel_id` AS `channel_id`,`t1`.`msisdn_num` AS `msisdn_num`,`t1`.`sum_amount` AS `sum_amount`,`t1`.`msg_num` AS `msg_num`,`t1`.`send_success_num` AS `send_success_num`,`t1`.`send_fail_num` AS `send_fail_num`,`t1`.`bill_success_num` AS `bill_success_num`,`t1`.`bill_fail_num` AS `bill_fail_num`,`t2`.`name` AS `channel_name` from (`ddo_channel_statistics_month` `t1` join `ddo_channel` `t2`) where (`t1`.`channel_id` = `t2`.`id`);
create view ddo_channel_statistics_day_view as select `t1`.`id` AS `id`,`t1`.`sum_date` AS `sum_date`,`t1`.`channel_id` AS `channel_id`,`t1`.`msisdn_num` AS `msisdn_num`,`t1`.`sum_amount` AS `sum_amount`,`t1`.`msg_num` AS `msg_num`,`t1`.`send_success_num` AS `send_success_num`,`t1`.`send_fail_num` AS `send_fail_num`,`t1`.`bill_success_num` AS `bill_success_num`,`t1`.`bill_fail_num` AS `bill_fail_num`,`t2`.`name` AS `channel_name` from (`ddo_channel_statistics_day` `t1` join `ddo_channel` `t2`) where (`t1`.`channel_id` = `t2`.`id`);